import React, {useEffect, useState} from "react";
import {Button, Card, Col, Container, Form, InputGroup, Row, Table} from "react-bootstrap";
import {useDispatch, useSelector} from "react-redux";
import {getRouteData, getRouteStations} from "../redux/apiMobileV2/ApiMobileV2Action";
import SkeletonTable from "../components/MyComponets/SkeletonTable";
import Skeleton from "react-loading-skeleton";

function RouteValueStations() {
    const dispatch = useDispatch();
    const id = useSelector(state => state.apiMobileV2Reducer.routeId);
    useEffect(() => {
        dispatch(getRouteStations(id));
        dispatch(getRouteData(id));
    }, []);
    const data = useSelector(state => state.apiMobileV2Reducer.routeStationsData);
    const dataBus = useSelector(state => state.apiMobileV2Reducer.timeBusData);
    const [status, setStatus] = useState(true);
    const kpp1 = id !== 0 ? data.kpp1 : 0;
    const kpp2 = id !== 0 ? data.kpp2 : 0;
    console.log(status)
    return (
        <>
            <Container fluid style={{background: "BAC5C8"}}>
                <Row>
                    <Col md="4">
                        <Card className="card-user">
                            <div className="card-image">
                                <img alt="..." src={require("assets/img/bus.jpeg").default}/>
                            </div>
                            <Card.Body>
                                <div className="author">
                                    <img alt="..." className="avatar border-gray"
                                         src={require("assets/img/faces/face-2.jpg").default}/>
                                    <h5 className="title">{dataBus ? dataBus.name : "Loading"}</h5>
                                </div>
                                <p className="description text-center">
                                    {dataBus ? dataBus.kpp1 : "Loading"} <br/>
                                    <i className="nc-icon nc-refresh-02 text-danger"/><br/>
                                    {dataBus ? dataBus.kpp2 : "Loading"}<br/>
                                    <Button variant="danger" onclick={()=>history.go(-1)}>Ortga qaytish</Button>{' '}
                                </p>
                            </Card.Body>
                            <hr/>
                            <div className="button-container mr-auto ml-auto">
                                <Button
                                    className="btn-simple btn-icon"
                                    href="#pablo"
                                    onClick={(e) => e.preventDefault()}
                                    variant="link"
                                >
                                    <i className="fab fa-facebook-square"/>
                                </Button>
                                <Button
                                    className="btn-simple btn-icon"
                                    href="#pablo"
                                    onClick={(e) => e.preventDefault()}
                                    variant="link"
                                >
                                    <i className="fab fa-twitter"/>
                                </Button>
                                <Button
                                    className="btn-simple btn-icon"
                                    href="#pablo"
                                    onClick={(e) => e.preventDefault()}
                                    variant="link"
                                >
                                    <i className="fab fa-google-plus-square"/>
                                </Button>
                            </div>
                        </Card>
                    </Col>
                    <Col md="8">
                        <Card className="strpied-tabled-with-hover">
                            <Card.Header>
                                <Card.Title as="h4"><i className="nc-icon nc-spaceship text-primary"/>
                                    {" "}{dataBus ? dataBus.name + " o`tadigan bekatlari" : <Skeleton/>}</Card.Title>
                                <p className="card-category">
                                    Here is a subtitle for this table
                                </p>
                                {/*<Form.Group controlId="exampleForm.ControlSelect1">*/}
                                {/*    <Form.Label>Example select</Form.Label>*/}
                                {/*    <Form.Control as="select">*/}
                                {/*        <option onClick={()=> setStatus(true)}>{kpp1 > 0 ? kpp1[0].sn : <Skeleton/>} ➡➡ {kpp2 ? kpp2[0].sn :*/}
                                {/*            <Skeleton/>}</option>*/}
                                {/*        <option onClick={()=> setStatus(false)}>{kpp2 ? kpp2[0].sn : <Skeleton/>} ➡➡ {kpp1 ? kpp1[0].sn :*/}
                                {/*            <Skeleton/>}</option>*/}
                                {/*    </Form.Control>*/}
                                {/*</Form.Group>*/}
                            </Card.Header>
                            <Card.Body className="table-full-width table-responsive px-0">
                                <Table className="table-hover table-striped">
                                    <thead>
                                    <tr>
                                        <th className="border-0"><i className="nc-icon nc-map-big"/></th>
                                        <th className="border-0">Name</th>
                                    </tr>
                                    </thead>
                                    <tbody>
                                    {kpp1 ? status ?
                                        kpp1.map((item, index) =>
                                            <tr key={index++}>
                                                <td style={{width: 43}}><i className="fab fa-google"/></td>
                                                <td>{item.sn}</td>
                                            </tr>
                                        ) : kpp2.map((item, index) =>
                                            <tr key={index++}>
                                                <td style={{width: 43}}><i className="fab fa-google"/></td>
                                                <td>{item.sn}</td>
                                            </tr>
                                        )
                                        : <SkeletonTable/>}
                                    </tbody>
                                </Table>
                            </Card.Body>
                        </Card>
                    </Col>
                </Row>

            </Container>
        </>
    );
}

export default RouteValueStations;
