/*!

=========================================================
* Light Bootstrap Dashboard React - v2.0.0
=========================================================

* Product Page: https://www.creative-tim.com/product/light-bootstrap-dashboard-react
* Copyright 2020 Creative Tim (https://www.creative-tim.com)
* Licensed under MIT (https://github.com/creativetimofficial/light-bootstrap-dashboard-react/blob/master/LICENSE.md)

* Coded by Creative Tim

=========================================================

* The above copyright notice and this permission notice shall be included in all copies or substantial portions of the Software.

*/
import React from "react";
import PerfectScrollbar from 'react-perfect-scrollbar'
import 'react-perfect-scrollbar/dist/css/styles.css';
import {Link, NavLink, useLocation} from "react-router-dom";

import {Nav} from "react-bootstrap";

function Sidebar({color, image, routes}) {
    const location = useLocation();
    const activeRoute = (routeName) => {
        return location.pathname.indexOf(routeName) > -1 ? "active" : "";
    };
    return (
        <div className="sidebar" data-image={image} data-color={color}>
            {/*<PerfectScrollbar>*/}
                <div
                    className="sidebar-background"
                    style={{
                        backgroundImage: "url(" + image + ")",
                    }}
                />
                <div className="sidebar-wrapper">
                    <div className="logo d-flex align-items-center justify-content-start">
                        <Link to="/admin/dashboard" className="simple-text logo-mini mx-1">
                            <div className="logo-img">
                                <img
                                    src={require("assets/img/reactlogo.svg").default}
                                    alt="..."
                                />
                            </div>
                        </Link>
                        <a className="simple-text" style={{marginLeft: 7}} href="http://www.creative-tim.com">
                            My Bus
                        </a>
                    </div>
                    <Nav>
                        {routes.map((prop, key) => {
                            if (!prop.redirect)
                                return (
                                    <li
                                        className={
                                            prop.upgrade ? "active active-pro" : activeRoute(prop.layout + prop.path)
                                        }
                                        key={key}
                                    >
                                        <NavLink to={prop.layout + prop.path} className="nav-link"
                                                 activeClassName="active">
                                            <i className={prop.icon}/>
                                            <p>{prop.name}</p>
                                        </NavLink>
                                    </li>
                                );
                            return null;
                        })}
                    </Nav>
                </div>
            {/*</PerfectScrollbar>*/}
        </div>
    );
}

export default Sidebar;
